import React, { Component } from 'react';
import './App.css';
import TasksList from './components/task-list';
import Header from './components/header';
class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      array:[
        {
          id: 1,
          name :"don dep"
        },
        {
          id: 1,
          name :"don dep"
        },
        {
          id: 1,
          name :"don dep"
        }
      ],
      isClick: true
    }
  }
  render() {

    let element = this.state.array.map((item,index)=>{
      return <TasksList 
      key={index}
      name= {item.name}
      content="task 1 content"
    />
    })
    return (
      <div>
        <Header />
        <div className="row">
            <div className="col-6">
                {element}
            </div>  

        </div>
        <button type="button" className="btn btn-outline-primary">
              Status: {this.state.isClick? 'true':'false'}
            </button>
        <button type="button" className="btn btn-primary"
        onClick={this.onClick}
        >

        click me</button> 

        <button type="button" className="btn btn-primary"
        onClick={this.onClick2('abc')}
        >
        click me 2
        </button>  
      </div>

    );
  }
  onClick2 = ()=>{
    console.log(this.refs);
  }
  onClick= ()=>{
    this.setState({
      isClick:!this.state.isClick
    })
  }
}

export default App;
