import React, { Component } from 'react';


class Task extends Component {
    render() {
        return (
                <li className="list-group-item">
                    <p>Content: {this.props.content}</p>

                    <button type="button" className="btn btn-primary">
                        click
                    </button>

                </li>
        );
    }

}

export default Task;
