import React, { Component } from 'react';
import './App.css';
import TasksList from './components/task-list';
import Header from './components/header';
import Task from './components/tasks';
class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      array: [
        {
          name: "don dep"
        },
        {
          name: "cai dat"
        },
        {
          name: "be vac"
        }
      ],
      isClick: true
    }
  }
  render() {
    let element = this.state.array.map((item,index)=>{
      return <Task key={index}  content={item.name}></Task>
    })
    console.log(element);

    return (
      <div>
        <div className="container">
          <div className="row">
            <div className="col-6">
              <div className="card">
                <div className="card-header">
                  First Column
               </div>
                <ul className="list-group list-group-flush">
                  {element}
                </ul>
              </div>
            </div>
            <div className="col-6">
              <div className="card">
                <div className="card-header">
                  First Column
               </div>
                <ul className="list-group list-group-flush">
                  {element}
                </ul>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
